sys_script_include.script:
var Utils = Class.create();
Utils.prototype = {
	initialize: function() {
		this.quoteTable = 'x_snc_chuck_quote';
	},

	_restCall : function() {
		try { 
			var r = new sn_ws.RESTMessageV2('x_snc_chuck.Chuck', 'get');

			var response = r.execute();
			var responseBody = response.getBody();
			var httpStatus = response.getStatusCode();
			gs.info('responseBody=' + responseBody);
			if (httpStatus == "200") {
				var jokeObj = JSON.parse(responseBody.replace(/&quot;/g,'"'));

				return jokeObj.value;
			}

			return;
		}
		catch(ex) {
			var message = ex.getMessage();
		}	
	},

	getQuote : function() {

		var quote = this._restCall();

		if (!quote) {
			return;
		}

		var quoteGR = this.quoteExists(quote.id);

		if (!quoteGR) {
			this.createQuote(quote);
		} else {
			this.updateQuote(quoteGR);
		}

		return quote;
	},

	createQuote : function(quoteObj) {

		var quote = new GlideRecord(this.quoteTable);

		quote.newRecord();
		quote.id = quoteObj.id;
		quote.joke = quoteObj.joke;
		quote.insert();
	},

	updateQuote : function(quoteGR) {

		var count = parseInt(quoteGR.getValue('count'), 10);

		++count;
		quoteGR.count = count;
		quoteGR.update();

	},

	quoteExists : function(id) {

		var quote = new GlideRecord(this.quoteTable);

		if (quote.get('id', id)) {
			return quote;
		}

		return;

	},

	type: 'Utils'
};
